        {{ form_errors(form.reference) }}
        <br>

        {{ form_label(form.libelle,\"Libellé :\") }}
        {{ form_widget(form.libelle,{'attr':{'class':'form-control'}} ) }}
        {{ form_errors(form.libelle) }}
        <br>
        {{ form_label(form.categorie,\"Catégorie :\") }}

        {{ form_widget(form.categorie,{'attr':{'class':'form-control'}} ) }}

        {{ form_errors(form.categorie) }}
        <br>
        {{ form_label(form.description,\"Description :\") }}
        {{ form_widget(form.description,{'attr':{'class':'form-control'}} ) }}
        {{ form_errors(form.description) }}
        <br>
        {{ form_label(form.prix,\"Prix :\") }}
        {{ form_widget(form.prix,{'attr':{'class':'form-control'}} ) }}
        {{ form_errors(form.prix) }}<br>

        {{ form_label(form.img,\"Image :\") }}
        <div class=\"form-control-file\">
        {{ form_widget(form.img,{'attr':{'class':'form-control'}} ) }}
        </div>
        {{ form_errors(form.img) }}
        <br></div>
    <button type=\"submit\" class=\"btn btn-primary \">{{ button_label|default('Ajouter') }}</button>
